import React from 'react';
import './InputNum.css'

// stateless component: no internal state management
const inputNum = (props) => {
    return (
        <div className="InputNum">
            <p onClick={props.myClick}>{props.cm} cm = {props.inch} inches</p>
            <input type="number" onChange={props.convert} placeholder="cm" display="inline-block"/>
            <input type="number" onChange={props.convert} placeholder="cm" display="inline-block"/>
        </div>
    )
};

export default inputNum;
