import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Person from './Person/Person.js';

class App extends Component {
  state={
    persons: [
      { name: 'Max', age: 28 },
      { name: 'Manu', age: 29 },
      { name: 'Jodie', age: 30 },
    ],
    cm: 0,
    inch: 0,
  };


      // inch: event.target.value,
      // inch: event.target.type === 'number'? parseFloat(event.target.value) * 0.393701 : null,


  convertCm2Inch = (event) => {
    this.setState({
      cm:event.target.value,
      inch: event.target.value == 0 ? parseFloat(event.target.value) * 0.393701 : '0',
    })
  };

  switchHandler = () => {
    // just replace all the states
    this.setState({
      cm: 10000,
    });
  };

  render() {
    return (
      <div className="App">
        <h1>Hi, I'am a React App</h1>
        <Person 
          cm={this.state.cm} 
          inch={this.state.inch}
          convert={this.convertCm2Inch}
          myClick={this.switchHandler}></Person>
      </div>
    );
  };
}

export default App;
