import React from 'react';
import './Person.css'

// stateless component: no internal state management
const person = (props) => {
    return (
        <div className="Person">
            <p>{props.cm} cm = {props.inch} inches</p>
            <input type="text" onChange={props.convert} />
        </div>
    )
};

export default person;
